const slide_1 = document.querySelector(".slide-1"),
  slide_2 = document.querySelector(".slide-2"),
  slide_3 = document.querySelector(".slide-3"),
  arrowRight = document.querySelector("#arrow-right"),
  arrowLeft = document.querySelector("#arrow-left");

document.querySelector("body").addEventListener("click", function(e) {
  if (e.target.id === "arrow-left") {
    if (arrowRight.style.display === "block") {
      slide_3.style.width = "100%";
      slide_3.style.left = "0";  
      arrowLeft.style.display = "none";
    }else {
        slide_2.style.width = "100%";
        slide_2.style.left = "0";
        arrowRight.style.display = "block";
       
    }
   
 
  }
  if (e.target.id === "arrow-right") {
    slide_2.style.width = "0";
    slide_2.style.left = "100%";
    
    arrowLeft.style.display = "block";
    if (slide_3.style.width === "100%") {
      slide_3.style.width = "0";
      slide_3.style.left = "100%";
      slide_2.style.width = "100%";
      slide_2.style.left = "0";
      
    }else{
        arrowRight.style.display = "none";
    }

  }
});
